export default {
    data: () => ({
        alerta: false,
        textoAlerta: "",
        tipoAlerta: "",
        loader: false,
        dialog: false,
    }),
    methods: {
        EnableAlert(texto, tipoAlerta) {
            this.alerta = true
            this.tipoAlerta = tipoAlerta
            this.textoAlerta = texto

            setTimeout(() => {
                this.alerta = false
            }, 2300)
        },
        getColor(status) {
            if (status == 'Cancelada') return 'error'
            else if (status == 'Aguardando') return 'warning'
            else return 'success'
        },

        RetornoErro(error) {
            debugger
            if (localStorage.getItem('token') == null) {
                this.$router.push('/')
                setTimeout(() => this.EnableAlert("Usuario não Autorizado", "error"), 2000)
                return
            }

            this.EnableAlert(error.response == undefined ? error.message : error.response.data, "error")
            console.log(error)
        },

        FormatDate(date) {
            if (!date) return null

            console.log(this.date)

            const [year, month, day] = date.split('-')
            return `${day}/${month}/${year}`
        },
        
        Refresh() {
            this.$forceUpdate();
        }
    }
}
