export default {
    data: () => ({
        alerta: false,
        textoAlerta: "",
        tipoAlerta: "",
    }),
    methods: {
        EnableAlert(texto, tipoAlerta) {
            this.alerta = true
            this.tipoAlerta = tipoAlerta
            this.textoAlerta = texto

            setTimeout(() => {
                this.alerta = false
            }, 2000)
        },
    }
}
