import api from '@/services/api';
import LoadComponent from '../LoadComponent.vue';
export default {
  components: {
    LoadComponent
  },
  name: 'CadastroEmpresaComponent',
  data: () => ({
    loader: false,
    localRazaoSocial: '',
    localCpfCnpj: '',
    localTelefone: '',
    localEmail: '',
    localEnderecoId: '',
    localConfiguracoesmpresaId: '',
    localDataCadastro: ''
  }),
  methods: {
    Salvar() {
      this.loader = !this.loader;
      api.put('Empresa', {
        Id: 6,
        Nome: this.razaoSocial,
        CpfCnpj: this.cnpj,
        Telefone: this.telefone,
        Email: this.email,
        EnderecoId: this.enderecoId,
        ConfiguracoesEmpresaId: this.configuracoesEmpresaId,
        DataCadastro: new Date().toISOString()
      }).then(response => {
        this.$emit('response', {
          success: true,
          response: response
        });
      }).catch(error => {
        this.$emit('response', {
          success: false,
          response: error
        });
      }).finally(() => this.loader = !this.loader);
    }
  },
  watch: {
    razaoSocial(val) {
      debugger;
      this.localRazaoSocial = val;
    }
  },
  props: {
    razaoSocial: String,
    cnpj: String,
    telefone: String,
    email: String,
    enderecoId: Number,
    configuracoesEmpresaId: Number
  }
};
