export default {
    data: () => ({
        alerta: false,
        textoAlerta: "",
        tipoAlerta: "",
    }),
    methods: {
        EnableAlert(texto, tipoAlerta) {
            this.alerta = true
            this.tipoAlerta = tipoAlerta
            this.textoAlerta = texto

            setTimeout(() => {
                this.alerta = false
            }, 2000)
        },
        getColor(status) {
            if (status == 'Cancelada') return 'red'
            else if (status == 'Em Andamento') return 'yellow'
            else return 'green'
        },
    }
}
