import api from '@/services/api';
import AutoCompleteFieldComponent from '../Field/AutoCompleteFieldComponent.vue';
import LoadComponent from '../LoadComponent.vue';
export default {
  components: {
    LoadComponent,
    AutoCompleteFieldComponent
  },
  name: 'CadastroEnderecoComponent',
  data: () => ({
    loader: false
  }),
  methods: {
    Salvar() {
      this.loader = !this.loader;
      api.put('Endereco', {
        Id: this.enderecoId,
        Logradouro: this.logradouro,
        Bairro: this.bairro,
        Numero: this.numero,
        Complemento: this.complemento,
        EnderecoId: this.enderecoId,
        Cep: this.configuracoesEmpresaId,
        CidadeId: this.cidadeId
      }).then(response => {
        this.$emit('response', {
          success: true,
          response: response
        });
      }).catch(error => {
        this.$emit('response', {
          success: false,
          response: error
        });
      }).finally(() => this.loader = !this.loader);
    }
  },
  props: {
    enderecoId: Number,
    logradouro: String,
    bairro: String,
    numero: String,
    complemento: String,
    cep: String,
    estado: String,
    cidade: String,
    cidadeId: Number
  }
};
