import api from '@/services/api';
import AutoCompleteFieldComponent from '../Field/AutoCompleteFieldComponent.vue';
import LoadComponent from '../LoadComponent.vue';
export default {
  components: {
    LoadComponent,
    AutoCompleteFieldComponent
  },
  name: 'CadastroEnderecoComponent',
  data: () => ({
    loader: false,
    localLogradouro: '',
    localBairro: '',
    localNumero: '',
    localComplemento: '',
    localCep: '',
    localCidadeId: ''
  }),
  methods: {
    Salvar() {
      this.loader = !this.loader;
      api.put('Endereco', {
        Id: this.enderecoId,
        Logradouro: this.logradouro,
        Bairro: this.bairro,
        Numero: this.numero,
        Complemento: this.complemento,
        Cep: this.cep,
        CidadeId: this.cidadeId
      }).then(response => {
        this.$emit('response', {
          success: true,
          response: response
        });
      }).catch(error => {
        this.$emit('response', {
          success: false,
          response: error
        });
      }).finally(() => this.loader = !this.loader);
    }
  },
  watch: {
    logradouro(val) {
      debugger;
      this.localLogradouro = val;
    },
    bairro(val) {
      this.localBairro = val;
    },
    numero(val) {
      this.localNumero = val;
    },
    complemento(val) {
      this.localComplemento = val;
    },
    cep(val) {
      this.localCep = val;
    },
    cidadeId(val) {
      this.localCidadeId = val;
    }
  },
  props: {
    enderecoId: Number,
    logradouro: String,
    bairro: String,
    numero: String,
    complemento: String,
    cep: String,
    estado: String,
    cidade: String,
    cidadeId: Number
  }
};
