export default {
  components: {
    EmpresasComponent,
    AgendarComponent,
    HistoricoClienteComponent,
    LoadComponent
  },
  name: "InicialComponent",
  data: () => ({
    loader: false,
    empresaComponent: false,
    agendaComponent: false,
    historicoClienteComponent: true,
    drawer: null,
    menus: [{
      Id: 1,
      title: 'Home',
      icon: 'mdi-view-dashboard'
    }, {
      Id: 2,
      title: 'Histórico',
      icon: 'mdi-forum'
    }, {
      Id: 3,
      title: 'Sair',
      icon: 'mdi-forum'
    }]
  }),
  methods: {
    AcessarLink(item) {
      this.loader = !this.loader;
      this.empresaComponent = false, this.agendaComponent = false, this.historicoClienteComponent = false;
      if (item.Id == 1) this.empresaComponent = true;
    }
  }
};
