import api from '@/services/api';
import AutoCompleteFieldComponent from '../Field/AutoCompleteFieldComponent.vue';
import LoadComponent from '../LoadComponent.vue';
export default {
  components: {
    LoadComponent,
    AutoCompleteFieldComponent
  },
  name: 'CadastroEnderecoComponent',
  data: () => ({
    loader: false,
    localLogradouro: '',
    localBairro: '',
    localNumero: '',
    localComplemento: '',
    localCep: '',
    localCidadeId: '',
    localCidade: '',
    localEstdo: ''
  }),
  methods: {
    Salvar() {
      this.loader = !this.loader;
      api.put('Endereco', {
        Id: this.enderecoId,
        Logradouro: this.localLogradouro,
        Bairro: this.localBairro,
        Numero: this.localNumero,
        Complemento: this.localComplemento,
        Cep: this.localCep,
        CidadeId: this.cidadeId
      }).then(response => {
        this.$emit('response', {
          success: true,
          response: response
        });
      }).catch(error => {
        this.$emit('response', {
          success: false,
          response: error
        });
      }).finally(() => this.loader = !this.loader);
    }
  },
  created() {
    this.localLogradouro = this.logradouro;
    this.localBairro = this.bairro;
    this.localNumero = this.numero;
    this.localComplemento = this.complemento;
    this.localCep = this.cep;
    this.localCidade = this.cidade;
    this.localEstado = this.estado;
    this.localCidadeId = this.cidadeId;
  },
  props: {
    enderecoId: Number,
    logradouro: String,
    bairro: String,
    numero: String,
    complemento: String,
    cep: String,
    estado: String,
    cidade: String,
    cidadeId: Number
  }
};
