import api from "@/services/api"

const restService = {
    methods: {
        async executeGet(url) {
            return await api.get(url)
            .then(response => {
                    return response.data;
                }
            )
            .catch(error => {
                    return error;
            });
        },
        async executePost(url, payload) {
           return await api.post(url, JSON.stringify(payload))
            .then(response => {
                return response.data;
            }
            )
            .catch(error => {
                    return error;
            });;
        },
        async executePut(url, payload) {
            return await api.put(url, JSON.stringify(payload));
        }
    },
}

export default restService;
