export default {
    data: () => ({
        alerta: false,
        textoAlerta: "",
        tipoAlerta: "",
        loader: false,
    }),
    methods: {
        EnableAlert(texto, tipoAlerta) {
            this.alerta = true
            this.tipoAlerta = tipoAlerta
            this.textoAlerta = texto

            setTimeout(() => {
                this.alerta = false
            }, 2000)
        },
        getColor(status) {
            if (status == 'Cancelada') return 'error'
            else if (status == 'Aguardando') return 'warning'
            else return 'success'
        },
    }
}
