import GenericMethods from '@/mixins/GenericMethods';
import AlertComponent from './AlertComponent.vue';
import LoadComponent from './LoadComponent.vue';
import RequestMethods from '@/mixins/RequestMethods';
import DialogPersistentComponent from './Field/DialogPersistentComponent.vue';
export default {
  components: {
    AlertComponent,
    LoadComponent,
    DialogPersistentComponent
  },
  mixins: [GenericMethods, RequestMethods],
  name: 'ServicoComponent',
  data: () => ({
    headers: [{
      text: 'Código',
      width: "150",
      value: 'Id'
    }, {
      text: 'Descricao',
      width: "750",
      value: 'Descricao'
    }, {
      text: 'Valor',
      value: 'Valor'
    }, {
      text: 'Ativo',
      value: 'Ativo'
    }, {
      text: 'Ações',
      value: 'actions',
      sortable: false,
      align: "center"
    }],
    servicos: [],
    descricao: null,
    ativo: 'Sim',
    valor: null,
    sortBy: 'Id',
    sortDesc: true
  }),
  methods: {
    novo() {
      this.acao = 'Cadastrar';
      this.descricao = null;
      this.dialog = !this.dialog;
    },
    retornoDialog(retorno) {
      if (retorno.acao == 'Cadastrar' && retorno.success) this.requestCadastrarServico();else if (retorno.acao == 'Editar' && retorno.success) this.requestEditarPeriodo(retorno.response);
      this.dialog = !this.dialog;
    },
    requestCadastrarServico() {
      this.loader = !this.loader;
      this.servicos = [];
      this.RequestPost('Servico', {
        Descricao: this.descricao,
        EmpresaId: this.dadosUsuario.EmpresaId,
        Valor: this.valor,
        Ativo: this.ativo == 'Sim'
      }, retorno => {
        this.EnableAlert("Concluido com sucesso.", "success");
      }, error => this.RetornoErro(error), () => {
        this.loader = !this.loader;
        this.requestBuscarPeriodoPorEmpresaId();
      });
    },
    requestEditarServico(item) {
      this.loader = !this.loader;
      this.servicos = [];
      this.RequestPut('Servico', {
        Id: item.Id,
        Descricao: this.descricao,
        Valor: this.valor,
        Ativo: this.ativo == 'Sim',
        EmpresaId: this.dadosUsuario.EmpresaId
      }, retorno => {
        this.EnableAlert("Concluido com sucesso.", "success");
      }, error => this.RetornoErro(error), () => {
        this.loader = !this.loader;
        this.requestBuscarPeriodoPorEmpresaId();
      });
    }
  }
};
