export default {
    data: () => ({
        alerta: false,
        textoAlerta: "",
        tipoAlerta: "",
    }),
    methods: {
        EnableAlert(texto, tipoAlerta) {
            this.alertaUsuario = true
            this.tipoAlerta = tipoAlerta
            this.textoAlerta = texto

            // setTimeout(() => {
            //     this.alertaUsuario = false
            // }, 1800)
        },
    }
}
