import "core-js/modules/es.array.push.js";
import GenericMethods from '@/mixins/GenericMethods';
import LoadComponent from '../LoadComponent.vue';
import AlertComponent from '../AlertComponent.vue';
import DialogPersistentComponent from '../Field/DialogPersistentComponent.vue';
import RequestMethods from '@/mixins/RequestMethods';
export default {
  components: {
    LoadComponent,
    AlertComponent,
    DialogPersistentComponent
  },
  name: 'CadastroPeriodoComponent.vue',
  mixins: [GenericMethods, RequestMethods],
  data: () => ({
    headers: [{
      text: 'Código',
      width: "150",
      value: 'Id'
    }, {
      text: 'Descricao',
      width: "750",
      value: 'Descricao'
    }, {
      text: 'Ativo',
      value: 'Ativo'
    }, {
      text: 'Ações',
      value: 'actions',
      sortable: false,
      align: "center"
    }],
    periodos: [],
    ativo: 'Sim',
    descricao: null,
    acao: null,
    itemEnvio: null,
    sortBy: 'Id',
    sortDesc: true
  }),
  methods: {
    novo() {
      this.acao = 'Cadastrar';
      this.descricao = null;
      this.dialog = !this.dialog;
    },
    requestBuscarPeriodoPorEmpresaId() {
      this.loader = !this.loader;
      this.RequestGet('Periodo/' + this.dadosUsuario.EmpresaId, retorno => this.RetornoPeriodo(retorno), error => this.$emit('response', {
        success: false,
        response: error
      }), () => this.loader = !this.loader);
    },
    RetornoPeriodo(retorno) {
      retorno.data.forEach(element => {
        this.periodos.push({
          Id: element.id,
          Descricao: element.descricao,
          Ativo: element.ativo ? 'Sim' : 'Não'
        });
      });
    },
    retornoDialog(retorno) {
      if (retorno.acao == 'Cadastrar' && retorno.success) this.requestCadastrarPeriodo();else if (retorno.acao == 'Editar' && retorno.success) this.requestEditarPeriodo(retorno.response);
      this.dialog = !this.dialog;
    },
    requestCadastrarPeriodo() {
      this.loader = !this.loader;
      this.periodos = [];
      this.RequestPost('Periodo', {
        Descricao: this.descricao,
        EmpresaId: this.dadosUsuario.EmpresaId,
        Ativo: this.ativo == 'Sim'
      }, retorno => {
        this.EnableAlert("Concluido com sucesso.", "success");
        window.scrollTo(0, 0);
      }, error => this.RetornoErro(error), () => {
        this.loader = !this.loader;
        this.requestBuscarPeriodoPorEmpresaId();
      });
    },
    deletarPeriodo(item) {
      this.loader = !this.loader;
      this.periodos = [];
      this.RequestDelete('Periodo/' + item.Id, retorno => {
        this.EnableAlert("Concluido com sucesso.", "success");
        window.scrollTo(0, 0);
      }, error => this.RetornoErro(error), () => {
        this.loader = !this.loader;
        this.requestBuscarPeriodoPorEmpresaId();
      });
    },
    editarPeriodo(item) {
      debugger;
      this.acao = 'Editar';
      this.itemEnvio = item;
      this.descricao = item.Descricao;
      this.ativo = item.ativo;
      this.dialog = !this.dialog;
    },
    requestEditarPeriodo(item) {
      this.loader = !this.loader;
      this.periodos = [];
      this.RequestPut('Periodo', {
        Id: item.Id,
        Descricao: this.descricao,
        EmpresaId: this.dadosUsuario.EmpresaId
      }, retorno => {
        this.EnableAlert("Concluido com sucesso.", "success");
        window.scrollTo(0, 0);
      }, error => this.RetornoErro(error), () => {
        this.loader = !this.loader;
        this.requestBuscarPeriodoPorEmpresaId();
      });
    }
  },
  created() {
    this.requestBuscarPeriodoPorEmpresaId();
  },
  props: {
    dadosUsuario: Object
  }
};
