import AgendarComponent from './AgendarComponent.vue';
import EmpresasComponent from './EmpresasComponent.vue';
import HistoricoClienteComponent from './HistoricoClienteComponent.vue';
import LoadComponent from './LoadComponent.vue';
export default {
  components: {
    EmpresasComponent,
    AgendarComponent,
    HistoricoClienteComponent,
    LoadComponent
  },
  name: "InicialComponent",
  data: () => ({
    dadosEmpresa: null,
    loader: false,
    empresaComponent: false,
    agendaComponent: false,
    historicoClienteComponent: true,
    drawer: null,
    menus: [{
      Id: 1,
      title: 'Home',
      icon: 'mdi-view-dashboard'
    }, {
      Id: 2,
      title: 'Histórico',
      icon: 'mdi-forum'
    }, {
      Id: 3,
      title: 'Sair',
      icon: 'mdi-forum'
    }]
  }),
  methods: {
    DesabilitarComponentes() {
      this.empresaComponent = false, this.historicoClienteComponent = false, this.agendaComponent = false;
    },
    AcessarLink(item) {
      this.loader = !this.loader;
      this.DesabilitarComponentes();
      if (item.Id == 1) this.empresaComponent = true;else if (item.Id == 2) this.historicoClienteComponent = true;
      setTimeout(() => {
        this.loader = !this.loader;
      }, 300);
    },
    RetornoEmpresa(empresa) {
      this.loader = !this.loader;
      this.DesabilitarComponentes();
      this.dadosEmpresa = empresa;
      this.agendaComponent = true;
      setTimeout(() => {
        this.loader = !this.loader;
      }, 300);
    }
  },
  mounted() {
    // console.log(this.$route.params.dadosUsuario)
    console.log(this.dadosUsuario);
  },
  props: {
    dadosUsuario: Object
  }
};
