  // },

  props: {
    razaoSocial: String,
    cnpj: String,
    telefone: String,
    email: String,
    //enderecoId: Number,
    //configuracoesEmpresaId: Number,
    dataCadastro: String,
    //empresaAdicionalId: Number,
    NameImagem: String,
    dadosUsuario: Object,
    retornoEmpresa: Object
  }
};
