    this.dadosUsuario = jwt_decode(localStorage.getItem('token'));
    this.TelasIniciais();
  },
  watch: {
    list() {
      if (this.$vuetify.breakpoint.name === "xs" || this.$vuetify.breakpoint.name === "md") {
        this.drawer = !this.drawer;
      }
    }
  }
};
