import api from '@/services/api';
import LoadComponent from '../LoadComponent.vue';
export default {
  components: {
    LoadComponent
  },
  name: 'CadastroEmpresaComponent',
  data: () => ({
    loader: false,
    localRazaoSocial: '',
    localCpfCnpj: '',
    localTelefone: '',
    localEmail: '',
    localEnderecoId: '',
    localConfiguracoesmpresaId: '',
    localDataCadastro: ''
  }),
  methods: {
    Salvar() {
      this.loader = !this.loader;
      api.put('Empresa', {
        Id: 6,
        Nome: this.localRazaoSocial,
        CpfCnpj: this.localCpfCnpj,
        Telefone: this.localTelefone,
        Email: this.localEmail,
        EnderecoId: this.localEnderecoId,
        ConfiguracoesEmpresaId: this.localConfiguracoesmpresaId,
        DataCadastro: this.dataCadastro
      }).then(response => {
        this.$emit('response', {
          success: true,
          response: response
        });
      }).catch(error => {
        this.$emit('response', {
          success: false,
          response: error
        });
      }).finally(() => this.loader = !this.loader);
    }
  },
  watch: {
    razaoSocial(val) {
      debugger;
      this.localRazaoSocial = val;
    },
    cnpj(val) {
      this.localCpfCnpj = val;
    },
    telefone(val) {
      debugger;
      this.localTelefone = val;
    },
    email(val) {
      this.localEmail = val;
    },
    enderecoId(val) {
      this.localEnderecoId = val;
    },
    configuracoesEmpresaId(val) {
      this.localConfiguracoesmpresaId = val;
    },
    datacadastro(val) {
      this.dataCadastro = val;
    }
  },
  props: {
    razaoSocial: String,
    cnpj: String,
    telefone: String,
    email: String,
    enderecoId: Number,
    configuracoesEmpresaId: Number,
    datacadastro: String
  }
};
