import api from "@/services/api"

const restService = {
    methods: {
        executeGet(url) {
            api.get(url)
            .then(response => {
                    return response.data;
                }
            )
            .catch(error => {
                    return error;
                });
        },
        executePost(url, payload) {
            return api.post(url, JSON.stringify(payload));
        },
        executePut(url, payload) {
            return api.put(url, JSON.stringify(payload));
        }
    },
}

export default restService;
