import RequestMethods from '@/mixins/RequestMethods';
import GenericMethods from '@/mixins/GenericMethods';
export default {
  name: 'CadastroUsuarioComponent',
  mixins: [RequestMethods, GenericMethods],
  data: () => ({
    localNomeCompleto: null,
    localTelefone: null,
    localUsuario: null,
    localSenha: null,
    localRepetirSenha: null,
    show1: false,
    rules: [v => !!v || 'Campo Obrigatório.'],
    numberRule: [v => /^\d+$/.test(v) || 'Campo aceita apenas numeros.']
  }),
  methods: {
    limparCampos() {
      if (this.clean) {
        this.localNomeCompleto = null;
        this.localUsuario = null;
        this.localSenha = null;
        this.localTelefone = null;
      }
    },
    ValidarCampos() {
      if (this.localNomeCompleto == null) return false;
      if (this.localUsuario == null) return false;
      if (this.localSenha == null) return false;
      if (this.localTelefone == null) return false;
    },
    Salvar() {
      if (this.ValidarCampos() == false) return;
      this.RequestPost('Usuario/InserirCliente', {
        Nome: this.localNomeCompleto,
        DataCadastro: new Date().toISOString(),
        Login: this.localUsuario,
        Senha: this.localSenha,
        Telefone: this.localTelefone
      }, retorno => this.RetornoUsuario(retorno), error => this.RetornoErroUsuario(error), () => this.loader = !this.loader);
    },
    RetornoUsuario(retorno) {
      debugger;
      this.responseDialog(true);
    },
    RetornoErroUsuario(error) {
      debugger;
      this.$emit('error', {
        success: false,
        response: error.response == undefined ? error.message : error.response.data
      });
    },
    responseDialog(retorno) {
      debugger;
      this.EnableAlert("Usuário cadastrado com sucesso.", "success");
      this.limparCampos();
      this.$emit('response', {
        success: retorno,
        response: this.item,
        acao: this.acao
      });
    }
  },
  watch: {
    salvar(newValue) {
      this.Salvar();
    },
    cancelar(newValue) {
      this.limparCampos();
    }
  },
  props: {
    salvar: Boolean,
    cancelar: Boolean,
    titulo: String,
    clean: Boolean
  }
};
