import api from '@/services/api';
import LoadComponent from '../LoadComponent.vue';
import GenericMethods from '@/mixins/GenericMethods';
export default {
  components: {
    LoadComponent
  },
  name: 'CadastroAdicionaisComponent',
  mixins: [GenericMethods],
  data: () => ({
    // exibirMensagemRapida: 'Não',
    // bloquearReserva: 'Não',
    // aceitaReservaAutomaticamente: 'Não',
    // permiteReagendarReserva: 'Não',
    // tempoPermitidoCancelamento: null,
    // mensagemRapida: null,
    items: ['Sim', 'Não'],
    rules: [v => v != null && v.length <= 25 || 'Máximo 25 characters']
  }),
  methods: {
    Salvar() {
      this.loader = !this.loader;
      api.put('EmpresaAdicional', {
        Id: this.configuracoesEmpresaId,
        BloqueiaReserva: this.bloquearReserva == "Sim" ? true : false,
        AceitaReservaAutomaticamente: this.aceitaReservaAutomaticamente == "Sim" ? true : false,
        PermiteReagendarReserva: this.permiteReagendarReserva == "Sim" ? true : false,
        ExibirMensagemRapida: this.exibirMensagemRapida == "Sim" ? true : false,
        TempoPermitidoCancelamento: this.tempoPermitidoCancelamento,
        MensagemRapida: this.mensagemRapida
      }).then(response => {
        this.$emit('response', {
          success: true,
          response: response
        });
      }).catch(error => {
        this.$emit('response', {
          success: false,
          response: error
        });
      }).finally(() => this.loader = !this.loader);
    }
  },
  props: {
    empresaAdicionalId: Number,
    exibirMensagemRapida: String,
    bloquearReserva: String,
    aceitaReservaAutomaticamente: String,
    permiteReagendarReserva: String,
    tempoPermitidoCancelamento: Number,
    mensagemRapida: String
  }
};
